#!/usr/bin/env bash

# PLEASE NOTE: This script was adapted from output generated by conda-smithy.

# tar -xf A.tar.bz2
AMBER16=`pwd`/amber16
# cp -rf recipe-prebuild $AMBER16/
FEEDSTOCK_ROOT=$(cd "$(dirname "$0")/.."; pwd;)
CONDA=./miniconda/bin/conda
DOCKER_IMAGE=centos:5

docker info

config=$(cat <<CONDARC

channels:
 - defaults # As we need conda-build
 - conda-forge
conda-build:
 root-dir: /feedstock_root/build_artefacts
show_channel_urls: true

CONDARC
)

cat << EOF | docker run -i \
                        -v ${AMBER16}:/amber16 \
                        -v ${FEEDSTOCK_ROOT}:/feedstock_root \
                        -a stdin -a stdout -a stderr \
                        $DOCKER_IMAGE \
                        bash || exit $?

export PYTHONUNBUFFERED=1

echo "$config" > ~/.condarc
# A lock sometimes occurs with incomplete builds. The lock file is stored in build_artefacts.
conda clean --lock

yum -y install gcc csh flex wget perl bzip2

# Embarking on 1 case(s).
    cd /amber16/
    export AMBERHOME=/amber16/
    ./AmberTools/src/configure_python
    $CONDA update --yes --all
    $CONDA install --yes conda-build
    $CONDA info
    $CONDA build recipe-prebuild --quiet || exit 1
    cp `$CONDA build --output recipe-prebuild` /feedstock_root/test-docker/
EOF
